mapper.select:
SELECT
	id, title, content, is_top, status, valid_date, type, creator_id, creator_time, update_time
FROM
	pf_sm_announcement
WHERE 1=1
	   AND title like #{title}
	   AND is_top = #{isTop}
	   AND valid_date >=#{startTime}
       AND valid_date <= #{endTime}

SELECT
    a.*,e.name as createName
FROM
    pf_sm_announcement a inner join pf_sm_employee e on a.creator_id=e.id
WHERE a.status=1 
       AND a.title like #{title}
       AND a.update_time >=#{startTime}
       AND a.update_time <= #{endTime}
order by a.update_time desc
